changeSet.sql:
  CREATE TABLE IF NOT EXISTS dictionary
  (
    id           uuid         not null
    constraint dictionary_pk
    primary key,
    code   varchar(50)         not null unique,
    name   varchar(256),
    description text
);

  comment on table dictionary is 'Услуги гостех без привязки к контрактам';
      comment on column dictionary.id is 'Идетнификатор справочника';
      comment on column dictionary.code is 'Код справочника';
      comment on column dictionary.name is 'Наименование справочника';
      comment on column dictionary.description is 'Описание справочника';
