changeSet.sql:
  CREATE TABLE IF NOT EXISTS company_unit
  (
    id           uuid         not null
    constraint company_unit_pk
    primary key,
    code   varchar(50)         not null unique,
    name   varchar(256) not null unique,
    description text
);

  comment on table company_unit is 'Услуги гостех без привязки к контрактам';
      comment on column company_unit.id is 'Идетнификатор';
      comment on column company_unit.code is 'Код подражделения';
      comment on column company_unit.name is 'Наименование';
      comment on column company_unit.description is 'Описание';
